<script>
    document.querySelector('#profile-data-show').addEventListener('click', function(event) {
        const profileShow = document.querySelector('#profile-show');
        profileShow.style.display = profileShow.style.display === 'block' ? 'none' : 'block';

        event.stopPropagation();
    });

    document.addEventListener('click', function(event) {
        const profileShow = document.querySelector('#profile-show');

        if (profileShow.style.display === 'block' && !profileShow.contains(event.target)) {
            profileShow.style.display = 'none';
        }
    });
</script>
